contracts/App3Dict.sol.content:
//SPDX-License-Identifier: UNLICENSED
//This contract controls app-wide settings.
//For the hackathon, the scaffold-eth "debug contracts" is a sufficient admin interface
//to modify app-wide settings; an improved app manager interface is prioritized out of the MVP.
pragma solidity >=0.8.0 <0.9.0;

import "./IGamesController.sol";
import "./PayableOwnable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract App3Dict is PayableOwnable, IGamesController {
	// State Variables
	// baseToken: The base unit other prices are denoted in,
	// which becomes more important when using Chainlink price feeds to accept payment in various tokens
	ERC20 private _baseToken; //baseToken() getter returns IERC20
	uint256 public gameSponsorMin;
	uint256 public questionSponsorMin;
	uint256 public defaultMaxQuestionBid; //< 2**255
	uint24 public sponsorFractionOfQuestionPool = 20*100000; // A percentage (e.g. 20 for 20%) * 10^5
	uint24 public defaultSponsorFractionOfOptionPool = 2*100000; // A percentage (e.g. 2 for 2%) * 10^5
	uint256 public publicGoodsPoolUnpaidBalance = 0;
	uint256 public publicGoodsPoolPaidOut = 0;
	bool public openToAnyLister = false; //true on testnet, usually
	mapping(address => bool) public approvedListers;

	event BaseTokenChanged(
		IERC20 indexed oldBaseToken,
		IERC20 indexed newBaseToken
	);

	event GameSponsorMinChanged(
		uint256 oldValue,
		uint256 newValue
	);

	event QuestionSponsorMinChanged(
		uint256 oldValue,
		uint256 newValue
	);

	event DefaultMaxQuestionBidChanged(
		uint256 oldValue,
		uint256 newValue
	);

	event SponsorFractionOfQuestionPoolChanged(
		uint24 oldValue,
		uint24 newValue
	);

	event DefaultSponsorFractionOfOptionPoolChanged(
		uint24 oldValue,
		uint24 newValue
	);

	event OpenToAnyListerChanged(
		bool oldValue,
		bool newValue
	);

	event ApprovedListerChanged(
		address lister,
		bool wasApproved,
		bool isApproved
	);

	event TipReceived(
		address donor,
		uint256 amount
	);

	event PublicGoodsPoolDonationReceived(
		address donor,
		uint256 amount,
		uint256 newPublicGoodsPoolUnpaidBalance
	);

	event PublicGoodsPoolPayout(
		address paidTo,
		uint256 amountPaidOut,
		uint256 newTotalPaidOut
	);

	event BaseTokensPayout(
		address paidTo,
		uint256 amountPaidOut
	);

	// Constructor: Called once on contract deployment
	// Check packages/hardhat/deploy/00_deploy_your_contract.ts
	constructor(
		address payable initialOwner,
		ERC20 baseTokenToSet
	)
		PayableOwnable(initialOwner)
	{
		_changeBaseToken(baseTokenToSet);
		// NOTE: The .decimals() function is not part of the ERC-20 standard interface (IERC20)!
		// Verify support before using any particular token in the constructor.
		_changeGameSponsorMin(100 * 10 ** ERC20(_baseToken).decimals());
		_changeQuestionSponsorMin(5 * 10 ** ERC20(_baseToken).decimals());
		//If the values of decimals is so high that this is beyond the range of MaxQuestionBid,
		//the private function will revert, to ensure the value is within the range.
		_changeDefaultMaxQuestionBid(100 * 10 ** ERC20(_baseToken).decimals());
	}

	function baseToken() public view returns (IERC20) {
		return _baseToken;
	}

	// Use with caution, especially if base token isn't a 1:1 value to the old!
	function changeBaseToken(
		ERC20 newBaseToken
	) public onlyOwner {
		_changeBaseToken(newBaseToken);
	}

	function _changeBaseToken(
		ERC20 newBaseToken
	) private {
		emit BaseTokenChanged(
			_baseToken,
			newBaseToken
		);
		_baseToken = newBaseToken;
	}

	function changeGameSponsorMin(
		uint256 newValue
	) public onlyOwner {
		_changeGameSponsorMin(newValue);
	}

	function _changeGameSponsorMin(
		uint256 newValue
	) private {
		emit GameSponsorMinChanged(
			gameSponsorMin,
			newValue
		);
		gameSponsorMin = newValue;
	}

	function changeQuestionSponsorMin(
		uint256 newValue
	) public onlyOwner {
		_changeQuestionSponsorMin(newValue);
	}

	function _changeQuestionSponsorMin(
		uint256 newValue
	) private {
		emit QuestionSponsorMinChanged(
			questionSponsorMin,
			newValue
		);
		questionSponsorMin = newValue;
	}

	function changeDefaultMaxQuestionBid(
		uint256 newValue
	) public onlyOwner {
		_changeDefaultMaxQuestionBid(newValue);
	}

	function _changeDefaultMaxQuestionBid(
		uint256 newValue
	) private {
		require(newValue < 2**255, 'Default max question bid is out of the allowed range.');
		emit DefaultMaxQuestionBidChanged(
			defaultMaxQuestionBid,
			newValue
		);
		defaultMaxQuestionBid = newValue;
	}

	function changeSponsorFractionOfQuestionPool(
		uint24 newValue
	) public onlyOwner {
		_changeSponsorFractionOfQuestionPool(newValue);
	}

	function _changeSponsorFractionOfQuestionPool(
		uint24 newValue
	) private {
		emit SponsorFractionOfQuestionPoolChanged(
			sponsorFractionOfQuestionPool,
			newValue
		);
		sponsorFractionOfQuestionPool = newValue;
	}

	function changeDefaultSponsorFractionOfOptionPool(
		uint24 newValue
	) public onlyOwner {
		_changeDefaultSponsorFractionOfOptionPool(newValue);
	}

	function _changeDefaultSponsorFractionOfOptionPool(
		uint24 newValue
	) private {
		emit DefaultSponsorFractionOfOptionPoolChanged(
			defaultSponsorFractionOfOptionPool,
			newValue
		);
		defaultSponsorFractionOfOptionPool = newValue;
	}

	function changeOpenToAnyLister(
		bool newValue
	) public onlyOwner {
		_changeOpenToAnyLister(newValue);
	}

	function _changeOpenToAnyLister(
		bool newValue
	) private {
		emit OpenToAnyListerChanged(
			openToAnyLister,
			newValue
		);
		openToAnyLister = newValue;
	}

	function changeApprovedLister(
		address lister,
		bool shouldBeApproved
	) public onlyOwner {
		_changeApprovedLister(lister, shouldBeApproved);
	}

	function _changeApprovedLister(
		address lister,
		bool shouldBeApproved
	) private {
		emit ApprovedListerChanged(
			lister,
			approvedListers[lister],
			shouldBeApproved
		);
		approvedListers[lister] = shouldBeApproved;
	}

	function donateToPublicGoods(
		uint256 amount
	) public {
		publicGoodsPoolUnpaidBalance += amount;
		emit PublicGoodsPoolDonationReceived(
			msg.sender, //must have prior allowance
			amount,
			publicGoodsPoolUnpaidBalance
		);
		require(_baseToken.transferFrom(msg.sender, address(this), amount), 'Donation transfer failed.');
	}

	function tip(
		uint256 amount
	) public {
		emit TipReceived(
			msg.sender, //must have prior allowance
			amount
		);
		require(_baseToken.transferFrom(msg.sender, address(this), amount), 'Tip transfer failed.');
	}

	function payoutPublicGoods(
		address payTo,
		uint256 payAmount
	) public onlyOwner {
		_payoutPublicGoods(
			payTo,
			payAmount
		);
	}

	function _payoutPublicGoods(
		address payTo,
		uint256 payAmount
	) private {
		require(payAmount <= publicGoodsPoolUnpaidBalance, 'Insufficient funds in public goods pool!');
		emit PublicGoodsPoolPayout(
			payTo,
			payAmount,
			publicGoodsPoolPaidOut + payAmount
		);
		publicGoodsPoolPaidOut += payAmount;
		publicGoodsPoolUnpaidBalance -= payAmount;
		require(_baseToken.transfer(payTo, payAmount), 'Public goods payout failed.');
	}

	function payoutBaseTokens(
		address payTo,
		uint256 payAmount
	) public onlyOwner {
		_payoutBaseTokens(
			payTo,
			payAmount
		);
	}

	function _payoutBaseTokens(
		address payTo,
		uint256 payAmount
	) private {
		require(
			payAmount <= (_baseToken.balanceOf(address(this))-publicGoodsPoolUnpaidBalance),
			'Insufficient funds to make the requested withdrawal!'
		);
		emit BaseTokensPayout(
			payTo,
			payAmount
		);
		require(_baseToken.transfer(payTo, payAmount), 'Payout failed.');
	}

	function withdrawERC20Tokens(
		address,
		uint256,
		IERC20
	) override public pure {
		revert('Arbitrary token withdrawal is disabled to protect the public goods balance.');
	}

	// If changing away from the controller architecture,
	// might need to change visibility here to public.
	function isAllowedToList(
		address potentialLister
	) external view returns (bool) {
		return (openToAnyLister || approvedListers[potentialLister]);
	}

}

contracts/Games.sol.content:
//SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0 <0.9.0;

import "./IGamesController.sol";
import "./IQuestionsController.sol";
import "./PayableOwnable.sol";

contract Games is PayableOwnable, IQuestionsController {

	struct Game {
		address lister;
		mapping(address => uint) sponsors; // uint is amount sponsored, in base tokens.
		uint totalSponsoredAmount;
		uint checkInStart;
		uint endTime; // for autopayout
		uint locationID; // supports map search in the future
		address[] referees; //accounts who can resolve or cancel a question, possibly also account => Boolean mapping and refCount uint.
		IERC20 gameToken; // The token address exchangeable 1:1 for tokens in the game; auto payout in that token after the game end time
		bool checkInRequired;
		bool openToAnyAsker;
		uint24 sponsorFractionOfOptionPool; // A percentage (e.g. 2 for 2%) * 10^5; can’t be changed after CheckInStart
		uint maxQuestionBid; // in token count per question per player. <2**255.
		mapping (address => int8) askerApprovals; //+1 for whitelist (only attened to if required), -1 for blacklist (though beware Sybils)
		// Some of the information could go into an off-chain metadata file,
		// with just one URL here.
		// Putting the image URI in the metadata file can be done,
		// but would slow down pageload especially on the frequently accessed event list page
		// due to an extra server lookup and roundtrip to get the image URL and then the image content.
		// If there is a large volume of metadata, off-chain is better,
		// but (a) that adds unnecessary complexity to the architecture
		// (for images, there's more argument favoring necessity)
		// and (b) on-chain events with events fired on every change help support server-side render caching
		// with reliable cache invalidation when data changes.
		string imageURI;
		string eventURI;
		string title140; // Up to 140 character title
		string descr500; // Up to 500 character description
		uint listStart; // for example, the published start time of a sports game.
		uint listEnd;
	}

	uint256 maxUsedID = 0; // 0 is not actually used, but reserved for the undefined/empty reference
	IGamesController controller;
	mapping(uint256 => Game) rows;

	event Creation(
		address indexed lister,
		uint indexed newId
	);

	event ControllerChanged(
		IGamesController indexed oldValue,
		IGamesController indexed newValue
	);

	event ListerChanged(
		uint indexed rowID,
		address indexed oldValue,
		address indexed newValue
	);

	event SponsorshipAdded(
		uint indexed rowID,
		address indexed sponsor,
		uint amountAdded,
		uint totalSponsorshipFromThisSponsor,
		uint totalSponsoredAmount
	);

	event CheckInStartChanged(
		uint indexed rowID,
		uint oldValue,
		uint newValue
	);

	event EndTimeChanged(
		uint indexed rowID,
		uint oldValue,
		uint newValue
	);

	event LocationIDChanged(
		uint indexed rowID,
		uint oldValue,
		uint newValue
	);

	event RefereeAdded(
		uint indexed rowID,
		address indexed referee
	);

	event RefereeRemoved(
		uint indexed rowID,
		address indexed referee
	);

	event GameTokenChanged(
		uint indexed rowID,
		IERC20 oldValue,
		IERC20 newValue
	);

	event CheckInRequiredChanged(
		uint indexed rowID,
		bool oldValue,
		bool newValue
	);

	event OpenToAnyAskerChanged(
		uint indexed rowID,
		bool oldValue,
		bool newValue
	);

	event SponsorFractionOfOptionPoolChanged(
		uint indexed rowID,
		uint24 oldValue,
		uint24 newValue
	);

	event MaxQuestionBidChanged(
		uint indexed rowID,
		uint oldValue,
		uint newValue
	);

	event AskerApprovalChanged(
		uint indexed rowID,
		address indexed asker,
		int8 oldValue,
		int8 newValue
	);

	event ImageURIChanged(
		uint indexed rowID,
		string oldValue,
		string newValue
	);

	event EventURIChanged(
		uint indexed rowID,
		string oldValue,
		string newValue
	);

	event Title140Changed(
		uint indexed rowID,
		string oldValue,
		string newValue
	);

	event Descr500Changed(
		uint indexed rowID,
		string oldValue,
		string newValue
	);

	event ListStartChanged(
		uint indexed rowID,
		uint oldValue,
		uint newValue
	);

	event ListEndChanged(
		uint indexed rowID,
		uint oldValue,
		uint newValue
	);

	error PropertyChangeAttemptByNonLister(address attempter);

	modifier onlyLister(uint rowID) {
		_checkLister(rowID); //Split out like OpenZeppelin's Ownable contract
		_;
	}

	function _checkLister(uint rowID) internal view virtual {
		if (rows[rowID].lister != msg.sender) {
			revert PropertyChangeAttemptByNonLister(_msgSender());
		}
	}

	constructor(
		address payable initialOwner,
		IGamesController initialController
	)
		PayableOwnable(initialOwner)
	{
		_changeController(initialController);
	}

	function changeController(
		IGamesController newController
	) public onlyOwner {
		_changeController(
			newController
		);
	}

	function _changeController(
		IGamesController newController
	) private {
		emit ControllerChanged(
			controller,
			newController
		);
		controller = newController;
	}

	function create(
		address lister
	) public {
		require(
			controller.isAllowedToList(lister),
			'This account is not currently allowed to create a new game.'
		);
		maxUsedID++;
		emit Creation(
			lister,
			maxUsedID
		);
		_changeLister(
			maxUsedID,
			lister
		);
		_changeGameToken(
			maxUsedID,
			controller.baseToken()
		);
		_changeSponsorFractionOfOptionPool(
			maxUsedID,
			controller.defaultSponsorFractionOfOptionPool()
		);
		_changeMaxQuestionBid(
			maxUsedID,
			controller.defaultMaxQuestionBid()
		);
	}

	function changeLister(
		uint rowID,
		address newValue
	) public onlyLister(rowID) {
		_changeLister(
			rowID,
			newValue
		);
	}

	function _changeLister(
		uint rowID,
		address newValue
	) private {
		emit ListerChanged(
			rowID,
			rows[rowID].lister,
			newValue
		);
		rows[rowID].lister = newValue;
	}

	function addSponsorship(
		uint rowID,
		uint amountToAdd,
		address sponsor
	) public {
		rows[rowID].sponsors[sponsor] += amountToAdd;
		rows[rowID].totalSponsoredAmount += amountToAdd;
		emit SponsorshipAdded(
			rowID,
			sponsor,
			amountToAdd,
			rows[rowID].sponsors[sponsor],
			rows[rowID].totalSponsoredAmount
		);
		require(
			rows[rowID].gameToken.transferFrom(msg.sender, address(controller), amountToAdd),
			'Sponsorship addition failed.'
		);
	}

	function changeCheckInStart(
		uint rowID,
		uint newValue
	) public onlyLister(rowID) {
		_changeCheckInStart(
			rowID,
			newValue
		);
	}

	function _changeCheckInStart(
		uint rowID,
		uint newValue
	) private {
		emit CheckInStartChanged(
			rowID,
			rows[rowID].checkInStart,
			newValue
		);
		rows[rowID].checkInStart = newValue;
	}

	function changeEndTime(
		uint rowID,
		uint newValue
	) public onlyLister(rowID) {
		_changeEndTime(
			rowID,
			newValue
		);
	}

	function _changeEndTime(
		uint rowID,
		uint newValue
	) private {
		emit EndTimeChanged(
			rowID,
			rows[rowID].endTime,
			newValue
		);
		rows[rowID].endTime = newValue;
	}

	function changeLocationID(
		uint rowID,
		uint newValue
	) public onlyLister(rowID) {
		_changeLocationID(
			rowID,
			newValue
		);
	}

	function _changeLocationID(
		uint rowID,
		uint newValue
	) private {
		emit LocationIDChanged(
			rowID,
			rows[rowID].locationID,
			newValue
		);
		rows[rowID].locationID = newValue;
	}

	function addReferee(
		uint rowID,
		address referee
	) public onlyLister(rowID) {
		_addReferee(
			rowID,
			referee
		);
	}

	function _addReferee(
		uint rowID,
		address referee
	) private {
		bool found = false;
		for(uint i = 0; i<rows[rowID].referees.length; i++) {
			if(rows[rowID].referees[i] == referee) {
				found = true;
			}
		}
		if(!found) {
			rows[rowID].referees.push(referee);
			emit RefereeAdded(
				rowID,
				referee
			);
		}
	}

	function removeReferee(
		uint rowID,
		address referee
	) public onlyLister(rowID) {
		_removeReferee(
			rowID,
			referee
		);
	}

	function _removeReferee(
		uint rowID,
		address referee
	) private {
		bool found = false;
		uint foundAt = 0;
		for(uint i = 0; i<rows[rowID].referees.length; i++) {
			if(rows[rowID].referees[i] == referee) {
				found = true;
				foundAt = i;
			}
		}
		if(found) {
			if(foundAt < rows[rowID].referees.length-1) {
				//if not the last element in the array, move the last element into the place being vacated
				rows[rowID].referees[foundAt] = rows[rowID].referees[rows[rowID].referees.length-1];
			}
			//Then drop the last element
			rows[rowID].referees.pop();
			emit RefereeRemoved(
				rowID,
				referee
			);
		}
	}

	/* TODO: Work through all the implications
	* of allowing a change to the game token
	* before enabling this function.
	function changeGameToken(
		uint rowID,
		IERC20 newToken
	) public onlyLister(rowID) {
		_changeGameToken(
			rowID,
			newToken
		);
	}
	*/

	function _changeGameToken(
		uint rowID,
		IERC20 newToken
	) private {
		emit GameTokenChanged(
			rowID,
			rows[rowID].gameToken,
			newToken
		);
		rows[rowID].gameToken = newToken;
	}

	function changeCheckInRequired(
		uint rowID,
		bool newValue
	) public onlyLister(rowID) {
		_changeCheckInRequired(
			rowID,
			newValue
		);
	}

	function _changeCheckInRequired(
		uint rowID,
		bool newValue
	) private {
		emit CheckInRequiredChanged(
			rowID,
			rows[rowID].checkInRequired,
			newValue
		);
		rows[rowID].checkInRequired = newValue;
	}

	function changeOpenToAnyAsker(
		uint rowID,
		bool newValue
	) public onlyLister(rowID) {
		_changeOpenToAnyAsker(
			rowID,
			newValue
		);
	}

	function _changeOpenToAnyAsker(
		uint rowID,
		bool newValue
	) private {
		emit OpenToAnyAskerChanged(
			rowID,
			rows[rowID].openToAnyAsker,
			newValue
		);
		rows[rowID].openToAnyAsker = newValue;
	}

	function changeSponsorFractionOfOptionPool(
		uint rowID,
		uint24 newValue
	) public onlyLister(rowID) {
		_changeSponsorFractionOfOptionPool(
			rowID,
			newValue
		);
	}

	function _changeSponsorFractionOfOptionPool(
		uint rowID,
		uint24 newValue
	) private {
		emit SponsorFractionOfOptionPoolChanged(
			rowID,
			rows[rowID].sponsorFractionOfOptionPool,
			newValue
		);
		rows[rowID].sponsorFractionOfOptionPool = newValue;
	}

	function changeMaxQuestionBid(
		uint rowID,
		uint newValue
	) public onlyLister(rowID) {
		_changeMaxQuestionBid(
			rowID,
			newValue
		);
	}

	function _changeMaxQuestionBid(
		uint rowID,
		uint newValue
	) private {
		require(newValue < 2**255, 'Max question bid is out of the allowed range.');
		emit MaxQuestionBidChanged(
			rowID,
			rows[rowID].maxQuestionBid,
			newValue
		);
		rows[rowID].maxQuestionBid = newValue;
	}

	function changeAskerApproval(
		uint rowID,
		address asker,
		int8 newValue
	) public onlyLister(rowID) {
		_changeAskerApproval(
			rowID,
			asker,
			newValue
		);
	}

	function _changeAskerApproval(
		uint rowID,
		address asker,
		int8 newValue
	) private {
		emit AskerApprovalChanged(
			rowID,
			asker,
			rows[rowID].askerApprovals[asker],
			newValue
		);
		rows[rowID].askerApprovals[asker] = newValue;
	}

	function changeImageURI(
		uint rowID,
		string calldata newValue
	) public onlyLister(rowID) {
		_changeImageURI(
			rowID,
			newValue
		);
	}

	function _changeImageURI(
		uint rowID,
		string calldata newValue
	) private {
		emit ImageURIChanged(
			rowID,
			rows[rowID].imageURI,
			newValue
		);
		rows[rowID].imageURI = newValue;
	}

	function changeEventURI(
		uint rowID,
		string calldata newValue
	) public onlyLister(rowID) {
		_changeEventURI(
			rowID,
			newValue
		);
	}

	function _changeEventURI(
		uint rowID,
		string calldata newValue
	) private {
		emit ImageURIChanged(
			rowID,
			rows[rowID].eventURI,
			newValue
		);
		rows[rowID].eventURI = newValue;
	}

	function changeTitle140(
		uint rowID,
		string calldata newValue
	) public onlyLister(rowID) {
		_changeTitle140(
			rowID,
			newValue
		);
	}

	function _changeTitle140(
		uint rowID,
		string calldata newValue
	) private {
		emit Title140Changed(
			rowID,
			rows[rowID].title140,
			newValue
		);
		rows[rowID].title140 = newValue;
	}

	function changeDescr500(
		uint rowID,
		string calldata newValue
	) public onlyLister(rowID) {
		_changeDescr500(
			rowID,
			newValue
		);
	}

	function _changeDescr500(
		uint rowID,
		string calldata newValue
	) private {
		emit Descr500Changed(
			rowID,
			rows[rowID].descr500,
			newValue
		);
		rows[rowID].descr500 = newValue;
	}

	function changeListStart(
		uint rowID,
		uint newValue
	) public onlyLister(rowID) {
		_changeListStart(
			rowID,
			newValue
		);
	}

	function _changeListStart(
		uint rowID,
		uint newValue
	) private {
		emit ListStartChanged(
			rowID,
			rows[rowID].listStart,
			newValue
		);
		rows[rowID].listStart = newValue;
	}

	function changeListEnd(
		uint rowID,
		uint newValue
	) public onlyLister(rowID) {
		_changeListEnd(
			rowID,
			newValue
		);
	}

	function _changeListEnd(
		uint rowID,
		uint newValue
	) private {
		emit ListEndChanged(
			rowID,
			rows[rowID].listEnd,
			newValue
		);
		rows[rowID].listEnd = newValue;
	}

	// If changing away from the controller architecture,
	// might need to change visibility here to public.
	function isAllowedToList(
		uint gameID,
		address potentialLister
	) external view returns (bool) {
		int8 approvalStatus = rows[gameID].askerApprovals[potentialLister];
		return (
			approvalStatus > 0 ||
			(rows[gameID].openToAnyAsker && approvalStatus >= 0)
		);
	}

	function isRefereeFor(
		uint gameID,
		address potentialReferee
	) external view returns (bool) {
		for(uint i=0; i<rows[gameID].referees.length; i++) {
			if(rows[gameID].referees[i] == potentialReferee) {
				return true;
			}
		}
		return false;
	}

	function gameToken(
		uint gameID
	) external view returns (IERC20) {
		return rows[gameID].gameToken;
	}

	function maxQuestionBid(
		uint gameID
	) external view returns (uint /*<2**255*/) {
		return rows[gameID].maxQuestionBid;
	}

	function sponsorFractionOfOptionPool(
		uint gameID
	) external view returns (uint24) {
		return rows[gameID].sponsorFractionOfOptionPool;
	}

}

