	//See https://github.com/ethereum/solidity/issues/12401
	function getEmptyUint16Array() private pure returns(uint16[] memory) {}

	function _markUnresolvable(
		uint rowID
	) private {
		emit Resolved(
			rowID,
			true,
			getEmptyUint16Array()
		);
		rows[rowID].unresolvable = true;
